mapper.select:
SELECT COUNT(*) FROM BOARD
AND board_type_id = #{boardTypeId, jdbcType=CHAR}
AND title LIKE CONCAT('%', #{searchQuery, jdbcType=VARCHAR}, '%')

SELECT b.board_id, b.member_id, b.board_type_id, b.title, b.content, b.view_count, b.status, b.reg_dt, b.updt_dt, b.del_dt, m.name FROM BOARD b JOIN MEMBER m ON b.member_id = m.member_id
AND b.board_type_id = #{searchBoardType, jdbcType=INTEGER}
AND b.title LIKE CONCAT('%', #{searchQuery, jdbcType=VARCHAR}, '%')
ORDER BY b.reg_dt DESC, b.title ASC LIMIT #{limitStart, jdbcType=INTEGER}, #{recordCnt, jdbcType=INTEGER}

mapper.delete:
DELETE
FROM BOARD
WHERE board_id = #{boardId, jdbcType=INTEGER}

mapper.update:
UPDATE BOARD
SET view_count = view_count + 1
WHERE board_id = #{boardId, jdbcType=INTEGER}
